if (args.test.options.healWounds) {
 if (args.test.succeeded) 
 {
   let wounds = this.actor.characteristics.int.bonus + Number(args.test.result.SL)
   if (args.test.options.fieldDressing && args.test.result.reversed)
   {
      wounds = this.actor.characteristics.int.bonus + Math.min(1, Number(args.test.result.SL))
   }
	args.test.result.woundsHealed = wounds
	args.test.result.other.push(`<b>${this.actor.name}</b> heilt <b>${wounds}</b> Wunden des Patienten.`)
   }
   else if (this.actor.characteristics.int.bonus + Number(args.test.result.SL) < 0)
   {
      args.test.result.other.push(`Dein Patient erleidet eine @UUID[Compendium.wfrp4e-core.items.Item.1hQuVFZt9QnnbWzg]{Infección leve}.`)
   }
}

if (args.test.options.staunchBleeding) 
{
 if (args.test.succeeded) 
 {
   let staunch =  Number(args.test.result.SL) + 1
   if (args.test.options.fieldDressing && args.test.result.reversed)
   {
     staunch =  Math.min(1, Number(args.test.result.SL)) + 1
   }
   args.test.result.other.push(`<b>${this.actor.name}</b> entfernt <b>${staunch}</b> Blutungszustände des Patienten.`)
 }
   else if (this.actor.characteristics.int.bonus + Number(args.test.result.SL) < 0)
   {
      args.test.result.other.push(`Dein Patient erleidet eine @UUID[Compendium.wfrp4e-core.items.Item.1hQuVFZt9QnnbWzg]{Infección leve}.`)
   }
}

if (!this.item.name.includes("(") || this.item.system.tests.value.toLowerCase().includes("(sinn)") || this.item.system.tests.value.toLowerCase().includes("(1 nach wahl)"))
{
