if (args.test.options.useOnesArgumentative && (args.test.result.roll <= game.settings.get("wfrp4e", "automaticSuccess") || args.test.result.roll <= args.test.target))
{

let SL = Math.floor(args.test.target / 10) - Math.floor(args.test.result.roll / 10)
let ones = Number(args.test.result.roll.toString().split("").pop())

if (ones > SL)
	args.test.data.result.SL = "+" + (ones + args.test.successBonus + args.test.slBonus)
   args.test.result.other.push(`<b>${this.effect.name}</b>: Würfel für die Einerstelle als EG verwendet.`)
}

if (args.test.options.useOnesAttractive && (args.test.result.roll <= game.settings.get("wfrp4e", "automaticSuccess") || args.test.result.roll <= args.test.target))
{

let SL = Math.floor(args.test.target / 10) - Math.floor(args.test.result.roll / 10)
let ones = Number(args.test.result.roll.toString().split("").pop())

if (ones > SL)
	args.test.data.result.SL = "+" + (ones + args.test.successBonus + args.test.slBonus)
   args.test.result.other.push(`<b>${this.effect.name}</b>: Würfel für die Einerstelle als EG verwendet.`)
}

if (args.test.options.beatBlade && (args.test.result.roll <= game.settings.get("wfrp4e", "automaticSuccess") || args.test.result.roll <= args.test.target))
{
   args.test.result.other.push(`<b>${this.effect.name}</b>: Gegner verliert ${Number(args.test.result.SL) + 1} Vorteile`)
}

if (this.actor.type != "character")
{
    return;
}

let god = await ValueDialog.create({text : "Wähle eine Gottheit", title :  "Segen"})

if (god)
{
    let prayers = await warhammer.utility.findAllItems("prayer", "Lade Gebete")
    let blessings = prayers.filter(p => p.system.god.value.split(",").map(i => i.trim().toLowerCase()).includes(god.toLowerCase()) && p.system.type.value == "blessing")
    let godBlessings = game.wfrp4e.config.godBlessings[god.toLowerCase()] || [];
    if (god == "Alter Glaube")
    {
        blessings = await ItemDialog.create(prayers.filter(i => i.system.type.value == "blessing"), 6, {text : "Wähle 6 beliebige Segen", title :  "Segen"})
    }
    if (godBlessings.length)
    {
        blessings = blessings.concat(await Promise.all(godBlessings.filter(bls => !(blessings.map(i => i.uuid).includes(bls.uuid))).map(fromUuid)));
    }
    if (blessings.length)
    {
        this.script.notification("Füge hinzu " + blessings.map(i => i.name).join(", "))
        await this.actor.createEmbeddedDocuments("Item", blessings, {fromEffect : this.effect.id})
    }
    else 
    {
        this.script.notification(`Konnte keine Segnungen finden für ${god}.`)
    }
    this.item.updateSource({name : this.item.name.replace("1 nach Wahl", god)})
    await this.actor.update({"system.details.god.value": god})
}

if (args.test.options.cardsharp && args.test.succeeded)
{

let SL = Math.floor(args.test.target / 10) - Math.floor(args.test.result.roll / 10)
let ones = Number(args.test.result.roll.toString().split("").pop())

if (ones > SL)
   args.test.result.other.push(`<span class="hide-option"><b>${this.effect.name}</b>: ${ones + args.test.successBonus + args.test.slBonus} EG.</span>`)
}

if (args.test.options.catfall && (args.test.result.roll <= game.settings.get("wfrp4e", "automaticSuccess") || args.test.result.roll <= args.test.target) && !args.test.result.catfall)
{
   args.test.result.other.push(`<b>${this.effect.name}</b>: Fallschaden Distanz reduziert um ${Number(args.test.result.SL) + 1} Meter.`)
   args.test.result.catfall = true // Prevent duplicate messages
}

if (args.test.options.income && !args.test.options.criminal)
{
    args.test.options.criminal = true;
    let currentCareer = args.test.actor.system.currentCareer;
    let coin = {1 : "b", 2 : "s", 3 : "s", 4 : "g"}[currentCareer.system.level.value] // b, s, or g maps to 2d10, 1d10, or 1 respectively (takes the first letter)
    let term = {1 : "G", 2 : "S", 3 : "S", 4 : "GK"}[currentCareer.system.level.value] // b, s, or g maps to 2d10, 1d10, or 1 respectively (takes the first letter)
    let dieAmount = {1 : "2d10", 2 : "1d10", 3 : "2d10", 4 : "1"}[currentCareer.system.level.value] // b, s, or g maps to 2d10, 1d10, or 1 respectively (takes the first letter)
    dieAmount = parseInt(dieAmount[0]) * this.item.system.Advances;     // Multilpy that first letter by your standing (Brass 4 = 8d10 pennies)
    if (coin != "g") // Don't roll for gold, just use standing value
    {
        dieAmount = dieAmount + "d10";
    }
    let moneyEarned = (await new Roll(dieAmount.toString()).roll()).total;
    let moneyString = `${moneyEarned}${coin}`
    let transactionString = `${moneyEarned}${term}`

    this.script.message(`<a class="money-drag" data-amt="${moneyString}"><strong>Zusätzlich ${game.wfrp4e.market.amountToString(game.wfrp4e.market.parseMoneyTransactionString(transactionString))} verdient</strong></a>`, {whisper : ChatMessage.getWhisperRecipients("GM")})
}

