if (this.item.system.tests.value.includes("(soziale Gruppe)"))
{
    let tests = this.item.system.tests.value
    let name = this.item.name

    // If name already specifies, make sure tests value reflects that
    if (name.includes("(") && !name.toLowerCase().includes("(1 nach wahl)"))
    {
        let group = name.split("(")[1].split(")")[0]
        tests = `${tests.split("(")[0].trim()} (${group})`
    }
    else
    {
        let value = await ValueDialog.create({text : "Wähle die soziale Gruppe zur Etikette", title : this.effect.name});
        if (value)
        {
            name = `${name.split("(")[0].trim()} (${value})`
            tests = this.item.system.tests.value.replace("soziale Gruppe", value);
        }
    }
    this.item.updateSource({name, "system.tests.value" : tests})
}

if (args.test.options.fieldDressing && args.test.result.reversed)
{
	args.test.data.result.SL = "+" + Math.min(1, Number(args.test.data.result.SL))
	args.test.result.other.push(`<b>${this.effect.name}</b>: EG limitiert auf 1`)
}

if (this.actor.hasCondition("stunned") || this.actor.hasCondition("unconscious"))
{
	this.script.notification("Deaktiviert!");
	await this.effect.update({"disabled" : true})
}

let group
let item = await fromUuid("Compendium.wfrp4e-core.items.Item.5hH73j2NgPdsLCZN");
let data = item.toObject();

if (this.item.name.includes("("))
{
	group = this.item.parenthesesText
}

else 
{
	group = await ValueDialog.create({text : "Gebe verhasste Gruppe ein", title : "Verhasste Gruppe"})

	this.item.updateSource({name : this.item.name + ` (${group})`, "system.tests.value" : this.item.system.tests.value.replace("Gruppe", group)})
	this.effect.updateSource({name : this.effect.name + ` (${group})`})
}


data.name = data.name.replace("Target", group);
await this.actor.createEmbeddedDocuments("Item", [data], {fromEffect: this.effect.id});

if (args.test.options.pilot && args.test.result.reversed)
{
	args.test.data.result.SL = "+" + Math.min(1, Number(args.test.data.result.SL))
	args.test.result.other.push(`<b>${this.effect.name}</b>: EG limitiert auf 1.`)
}

let resistance
if (this.item.name.includes("(") && !this.item.name.toLowerCase().includes("(1 nach wahl)"))
{
	resistance = this.item.parenthesesText
}
else 
{
    resistance = await Dialog.wait({
        title : "Gefahr",
                content : "<input type='text'>",
        buttons : {
                    confirm : {
                label : game.i18n.localize("Confirm"),
                        callback : (dlg) => {
                    return dlg.find("input")[0].value
        }
                    }
                },
	default : "confirm",
                close : () => {
            return ""
                }
            })

    if (resistance)
    {
        this.item.updateSource({name :  `${this.item.name.split("(")[0].trim()} (${resistance})`})
        this.effect.updateSource({name : this.effect.name + ` (${resistance})`})
    }
}    
this.item.updateSource({"system.tests.value" : this.item.system.tests.value.replace("die entsprechende Gefahr", resistance)})

if (resistance && !this.effect.name.includes("("))
{
    this.effect.updateSource({name : this.effect.name += ` (${resistance})`})
}

if (this.item.name.includes("("))
{
	return;
}

let index = game.packs
.filter(i => i.metadata.type == "Item")
.reduce((acc, pack) => acc.concat(pack.index.contents), [])
.filter(i => i.type == "skill" && i.name.includes(game.i18n.localize("NAME.Lore")))
.map(i => {
	i.id = i._id
	return i
})

let choice = await ItemDialog.create(index, 1, "Wähle Fachwissen")
let text;
if (!choice[0])
{
    let custom = await Dialog.wait({
        title : "Gebe Fachwissen ein",
        content : "<input type='text'>",
        buttons : {
            confirm : {
                label : game.i18n.localize("Confirm"),
                callback : (dlg) => {
                    return dlg.find("input")[0].value
                }
            }
        },
        default : "confirm",
        close : () => {
            return ""
        }
    })   
    text = custom || ""
}
else 
{
    text = game.wfrp4e.utility.extractParenthesesText(choice[0].name)
}

await this.item.updateSource({name : this.item.name + ` (${text})`, "system.tests.value" : this.item.system.tests.value.replace("zugehöriges Wissen", text)})
await this.effect.updateSource({name : this.effect.name + ` (${text})`})

if (args.opposedTest.result.winner == "attacker") {
  if (args.opposedTest.defenderTest.weapon && args.opposedTest.defenderTest.item.properties.qualities.shield) {
    ui.notifications.notify(`<b>${this.effect.name}</b>: ${this.item.Advances} Vorteile erhalten`)
    this.actor.setAdvantage(this.item.Advances)
  }
}

if (!this.item.name.includes("(") || this.item.system.tests.value.includes("Gelände") || this.item.system.tests.value.toLowerCase().includes("(1 nach wahl)"))
{
    let tests = this.item.system.tests.value
    let name = this.item.name

    // If name already specifies, make sure tests value reflects that
    if (name.includes("(") && !name.toLowerCase().includes("(1 nach wahl)"))
    {
        let terrain = name.split("(")[1].split(")")[0]
        tests = tests.replace("des Geländes", terrain)
    }
    else // If no sense specified, provide dialog choice
    {
        let choice = await ItemDialog.create(ItemDialog.objectToArray({
            coastal : "Küste",
            deserts : "Wüste",
            marshes : "Sumpf",
            rocky : "Felsig",
            tundra : "Tundra",
            woodlands : "Waldland"
        }, this.item.img), 1, "Wähle Gelände");
        if (choice[0])
        {
            name = `${name.split("(")[0].trim()} (${choice[0].name})`
            tests = tests.replace("des Geländes", "von Gelände des Typs " + choice[0].name)
        }
    }

    this.effect.updateSource({name})
    this.item.updateSource({name, "system.tests.value" : tests})
}

if (args.test.options.useOnesSupportive && (args.test.result.roll <= game.settings.get("wfrp4e", "automaticSuccess") || args.test.result.roll <= args.test.target))
{

let SL = Math.floor(args.test.target / 10) - Math.floor(args.test.result.roll / 10)
let ones = Number(args.test.result.roll.toString().split("").pop())

if (ones > SL)
	args.test.data.result.SL = "+" + (ones + args.test.successBonus + args.test.slBonus)
   args.test.result.other.push(`<b>${this.effect.name}</b>: Würfel für die Einerstelle als EG verwendet.`)
}

let skill = `Sprache (Magick)`
let currentCareer = this.actor.system.currentCareer;
let existingSkill = this.actor.itemTypes.skill.find(i => i.name == skill);

if (!currentCareer) return


let inCurrentCareer = currentCareer.system.skills.concat(currentCareer.system.addedSkills).includes(skill);
let witchAdded = this.actor.getFlag("wfrp4e", "witchAdded") || {};
if (existingSkill && inCurrentCareer && !witchAdded[existingSkill.name])
{
	existingSkill.system.advances.costModifier = -5;
}
else 
{
	witchAdded[skill] = true;
	currentCareer.system.addedSkills.push(skill);
	foundry.utils.setProperty(this.actor, "flags.wfrp4e.witchAdded", witchAdded)
}
